mapper.insert:
    
  INSERT INTO blood_sugar(
	blood_sugar_no
	, member_no
	, fasting_blood_sugar
	, fasting_state
	, blood_sugar_date
	)
	VALUES (
	 #{bloodSugarNo}
	, #{memberNo} 
	, #{fastingBloodSugar}
	, #{fastingState}
	, now()
	)

mapper.update:
		UPDATE blood_sugar
		SET blood_sugar_no =  #{bloodSugarNo}
	, member_no = #{memberNo} 
	, fasting_blood_sugar = #{fastingBloodSugar}
	, fasting_state = #{fastingState}
	, blood_sugar_date = now()
	WHERE blood_sugar_no = #{bloodSugarNo}
